import styled from "styled-components";
import { createGlobalStyle } from "styled-components";

export const GlobalStyle = createGlobalStyle`
  * {
    box-sizing: border-box;
    margin: 0;
    padding: 0;
  }
`;
// Styled component definition

export const Container = styled.div`
    box-sizing: border-box;
    display: flex;
    justify-content: center;
    flex-direction: column;
    /* align-items: center; */
`;

export const ChildContainer1 = styled.div`
    box-sizing: border-box;
    background-size: cover;
    position: relative;
    background-position: center;
    display: flex;
    justify-content: end;
    padding: 30px;
    color: white;
    height: 50vh;
    flex-direction: column;
    background-image: url(https://assets.ccbp.in/frontend/react-js/music-playlist/music-playlist-Edsheeran-bg.png);
`;
export const ChildContainer2 = styled.div`
    height: 50vh;
    background-color: #152850;
    display: flex;
    justify-content: center;
    padding: 20px;
    /* align-items: center; */
`;

export const Span = styled.span`
    font-size: 30px;
    color: white;
`;

export const SongsHeader = styled.div`
    display: flex;
    width: 100%;
    justify-content: space-between;
    align-items: flex-start;
    color: white;
`;

export const Input = styled.input`
    padding: 10px;
    box-sizing: border-box;
    /* Add other styling as needed */
`;
export const SearchIcon = styled.div`
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    left: 10px; /* Adjust the left spacing as needed */
    color: #ccc; /* Adjust the color as needed */
    cursor: pointer;
`;

export const H1 = styled.h1`
    margin: 0; /* Reset margin to avoid spacing issues */
`;
